mapper.select:
select

from courseware
where
 id > #{pageStartId,jdbcType=BIGINT}
 and course_id = #{courseId}
order by id limit #{countPerPage}

select

from
(
select

from courseware
where  id < #{pageEndId,jdbcType=BIGINT}
 and course_id = #{courseId}
order by id desc limit #{countPerPage}
) U
order by U.id ASC
