mapper.select:
select

from member
where name = #{name,jdbcType=VARCHAR} limit 1;

select 
 
from `member`
 where id > #{pageStartId,jdbcType=BIGINT}
and group_id = #{groupId,jdbcType=INTEGER} 
order by id ASC limit #{countPerPage,jdbcType=BIGINT}

select 
 
from 
(
	select 
	
	from `member` 
	 where id < #{pageEndId,jdbcType=BIGINT}  
	and group_id = #{groupId,jdbcType=INTEGER} 
	order by id DESC limit #{countPerPage,jdbcType=BIGINT} 
) SQ 
order by SQ.id ASC
